mapper.select:
SELECT RES.PROC_INST_ID_ as 'processInstanceId' , RES.BUSINESS_KEY_ as 'businessKey', P.KEY_ AS 'processDefinitionKey', P.ID_ AS 'processDefinitionId', P.NAME_ AS 'processDefinitionName', P.VERSION_ AS 'processDefinitionVersion', P.DEPLOYMENT_ID_ AS 'deploymentId' ,P.DGRM_RESOURCE_NAME_ as 'dgrmResourceName' ,hip.START_TIME_ as 'startTime' ,hip.END_TIME_ as 'endTime' ,hip.duration_ as 'duration' ,hip.start_user_id_ as 'startUserId'
,hia.TASK_ID_ as 'taskId' ,hia.ACT_NAME_ as 'actName' ,hia.ACT_TYPE_ as 'actType' ,hia.ASSIGNEE_ as 'assignee' FROM ACT_RU_EXECUTION RES INNER JOIN ACT_RE_PROCDEF P ON RES.PROC_DEF_ID_ = P.ID_ JOIN ACT_HI_PROCINST hip ON hip.PROC_INST_ID_ =RES.PROC_INST_ID_ LEFT JOIN ACT_HI_ACTINST hia ON hia.PROC_INST_ID_ =RES.PROC_INST_ID_ AND hia.ACT_ID_ = RES.ACT_ID_
RES.PARENT_ID_ IS NULL
and P.DEPLOYMENT_ID_=#{pid.deploymentId}
and P.ID_ =#{pid.processDefinitionId}
and P.NAME_ =#{pid.processDefinitionName}
and P.KEY_ =#{pid.processDefinitionKey}
and hia.ASSIGNEE_ =#{pid.assignee}
and hia.TASK_ID_ =#{pid.taskId}
and hia.ACT_NAME_ =#{pid.actName}
LIMIT #{pid.pageSize} OFFSET #{pid.offset}

SELECT
 count(*)
FROM
ACT_RU_EXECUTION RES
INNER JOIN ACT_RE_PROCDEF P ON RES.PROC_DEF_ID_ = P.ID_
JOIN ACT_HI_PROCINST hip ON hip.PROC_INST_ID_ =RES.PROC_INST_ID_
LEFT JOIN ACT_HI_ACTINST hia ON hia.PROC_INST_ID_ =RES.PROC_INST_ID_ AND
hia.ACT_ID_ = RES.ACT_ID_

	RES.PARENT_ID_ IS NULL

		and P.DEPLOYMENT_ID_=#{pid.deploymentId}



		and P.ID_ =#{pid.processDefinitionId}



		and P.NAME_ =#{pid.processDefinitionName}



		and P.KEY_ =#{pid.processDefinitionKey}




		and hia.ASSIGNEE_ =#{pid.assignee}


		and hia.TASK_ID_ =#{pid.taskId}



		and hia.ACT_NAME_ =#{pid.actName}
